<?php session_start();require_once 'common.php';require_once'db.php';dbConnect("higherdb");$config_basedir = "http://www.gethighered.ca";if($_SESSION['loggedin']==1){    error('Already logged into another account!');}$user = $_POST['Email'];$password =$_POST['Password'];$loginsql = "SELECT * FROM users WHERE username = '$user' AND password = PASSWORD('$password')";$loginres = mysql_query($loginsql);if(!$loginres){    error('query failed');}$numrows = mysql_num_rows($loginres);$redir=dirname("/srvr_scripts")."myprofile";		if($numrows !=0){//If login is correct$loginrow = mysql_result($loginres,0,'uid');	$getoken = mysql_query("SELECT tokens FROM token WHERE uid='$loginrow'");$formfill = mysql_result($loginres,0,'formsave');$tokenum=mysql_result($getoken,0);$_SESSION['loggedin'] = 1;$_SESSION['sessname'] = $user;$_SESSION['sessuid'] = $loginrow;$_SESSION['tokenum']= $tokenum;$_SESSION['formsave']= $formfill;header("Location: " . $config_basedir . "/dashboard");}else{error('Invalid Username or Password');header("Location: " . $config_basedir . "/signup");}?>